mapper.select:
SELECT A.USER_ID as USER_ID,
       A.INSERT_DATE as INSERT_DATE
  FROM TUSER A
 WHERE A.USER_ID=#{id}
   AND A.USER_PWD=#{pwd}

mapper.update:
UPDATE TBOARD A
   SET A.USE_YN = '0'
 WHERE A.NO = #{no}

UPDATE TBOARD A
   SET A.TITLE = #{title},
       A.CONTENT = #{content}
 WHERE A.NO = #{no}

mapper.select:
SELECT COUNT(1) AS ID_CHECK
  FROM TUSER A
 WHERE A.USER_ID = #{id}
